import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function SearchResults() {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation(); // Get the current location (URL) to extract query parameters

  const query = new URLSearchParams(location.search).get('q'); // Get 'q' parameter from the URL

  useEffect(() => {
    if (query) {
      const fetchSearchResults = async () => {
        try {
          const response = await axios.get(`https://ram.freelogomaker.in/public/api/products/search?search=${query}`);
          setSearchResults(response.data);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching search results:', error);
          setLoading(false);
        }
      };

      fetchSearchResults();
    }
  }, [query]); // Only run effect when the query changes

  return (
    <div className="container">
      <h2>Search Results for "{query}"</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="search-results">
          {searchResults.length > 0 ? (
            <ul>
              {searchResults.map((item) => (
                <li key={item.id}>
                  <a href={`/product/${item.id}`}>{item.name}</a>
                </li>
              ))}
            </ul>
          ) : (
            <p>No results found.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default SearchResults;
