import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import jsPDF from 'jspdf';
import '../css/CartModal.css';

function CartModal({ isOpen, onClose, updateQuantity, removeItem }) {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isCheckout, setIsCheckout] = useState(false); // State to toggle between cart and checkout form
  const [userDetails, setUserDetails] = useState({
    name: '',
    address: '',
    email: '',
    paymentMethod: '',
  });

  useEffect(() => {
    if (isOpen) {
      const fetchProducts = async () => {
        try {
          const token = localStorage.getItem("token");

          if (!token) {
            throw new Error("User is not logged in.");
          }

          const response = await axios.get("https://ram.freelogomaker.in/api/cart", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          const cartData = response.data?.cart || [];
          const cartProducts = response.data?.cartproduct || [];

          const mergedCartItems = cartData.map((cartItem) => {
            const product = cartProducts.find((product) => product.id === cartItem.product_id);
            return product ? { ...cartItem, ...product } : null;
          }).filter(Boolean);

          setCartItems(mergedCartItems);
          setLoading(false);
        } catch (error) {
          setError(error.message);
          setLoading(false);
        }
      };

      fetchProducts();
    }
  }, [isOpen]);

  const isCartEmpty = cartItems.length === 0;

  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => {
      const itemPrice = parseFloat(item.price);
      return sum + itemPrice * item.quantity;
    }, 0);
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    setCartItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  const handleCheckoutClick = () => {
    setIsCheckout(true); // Show the checkout form when "Proceed to Checkout" is clicked
  };

  const handleCheckoutSubmit = (e) => {
    e.preventDefault();
    generatePDF();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    doc.text('Invoice', 10, 10);
    doc.text(`Name: ${userDetails.name}`, 10, 20);
    doc.text(`Email: ${userDetails.email}`, 10, 30);
    doc.text(`Address: ${userDetails.address}`, 10, 40);
    doc.text(`Payment Method: ${userDetails.paymentMethod}`, 10, 50);

    // Add cart items (for now, it's just a placeholder)
    doc.text('Cart Items:', 10, 60);
    cartItems.forEach((item, index) => {
      doc.text(`${item.pname} - $${(parseFloat(item.price) * item.quantity).toFixed(2)}`, 10, 70 + index * 10);
    });

    // Save the PDF
    doc.save('invoice.pdf');
  };

  return (
    <div className={`modal-overlay ${isOpen ? 'show' : ''}`}>
      <div className="modal-content">
        <div className="modal-header">
          <h2>{isCheckout ? 'Checkout' : `Shopping Cart (${cartItems.length} items)`}</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        {!isCheckout ? (
          <div className="cart-items">
            {loading ? (
              <p className="loading-text">Loading...</p>
            ) : error ? (
              <p className="error-text">{error}</p>
            ) : isCartEmpty ? (
              <p className="empty-cart">Your cart is empty!</p>
            ) : (
              cartItems.map((item) => (
                <div key={item.id} className="cart-item">
                  <img src={item.pimage} alt={item.pname} className="item-image" />
                  <div className="item-details">
                    <h3>{item.pname}</h3>
                    <p className="item-price">${(parseFloat(item.price) * item.quantity).toFixed(2)}</p>
                    <div className="item-actions">
                      <div className="quantity-controls">
                        <button
                          className="quantity-btn"
                          onClick={() => handleQuantityChange(item.id, Math.max(1, item.quantity - 1))}
                        >
                          −
                        </button>
                        <span className="quantity-display">{item.quantity}</span>
                        <button
                          className="quantity-btn"
                          onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                        >
                          +
                        </button>
                      </div>
                      <button
                        onClick={() => removeItem(item.id)}
                        className="remove-button"
                      >
                        <i className="fas fa-trash-alt"></i> Remove
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}

            {!isCartEmpty && !loading && !error && (
              <div className="cart-summary">
                <div className="subtotal">
                  <span>Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items):</span>
                  <span className="total-price">${calculateTotal().toFixed(2)}</span>
                </div>
                <button className="checkout-button" onClick={handleCheckoutClick}>
                  Proceed to Checkout
                </button>
              </div>
            )}
          </div>
        ) : (
          <div className="checkout-form">
            <form onSubmit={handleCheckoutSubmit}>
              <div>
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  value={userDetails.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label>Email:</label>
                <input
                  type="email"
                  name="email"
                  value={userDetails.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label>Address:</label>
                <textarea
                  name="address"
                  value={userDetails.address}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label>Payment Method:</label>
                <select
                  name="paymentMethod"
                  value={userDetails.paymentMethod}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Payment Method</option>
                  <option value="Credit Card">Credit Card</option>
                  <option value="PayPal">PayPal</option>
                  <option value="Bank Transfer">Bank Transfer</option>
                </select>
              </div>
              <button type="submit">Submit Order</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default CartModal;
