import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/CartModal.css';

function CartModal({ isOpen, onClose, updateQuantity, removeItem }) {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch cart items from the API
  useEffect(() => {
    if (isOpen) {
      const fetchProducts = async () => {
        try {
          const token = localStorage.getItem("token");

          if (!token) {
            throw new Error("User is not logged in.");
          }

          const response = await axios.get("https://ram.freelogomaker.in/api/cart", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          // Merge cart items with product details
          const cartData = response.data?.cart || [];
          const cartProducts = response.data?.cartproduct || [];

          const mergedCartItems = cartData.map((cartItem) => {
            const product = cartProducts.find((product) => product.id === cartItem.product_id);
            return product ? { ...cartItem, ...product } : null; // Merge product details with cart item
          }).filter(Boolean); // Remove null items if product is not found

          setCartItems(mergedCartItems); // Set merged cart items
          setLoading(false);
        } catch (error) {
          setError(error.message);
          setLoading(false);
        }
      };

      fetchProducts();
    }
  }, [isOpen]);

  const isCartEmpty = cartItems.length === 0;

  // Convert price to number and calculate total price after each update
  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => {
      const itemPrice = parseFloat(item.price); // Convert price to number
      return sum + itemPrice * item.quantity;
    }, 0);
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    setCartItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  const handleCheckout = () => {
    onClose();
    navigate('/checkout');
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Shopping Cart ({cartItems.length} items)</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        <div className="cart-items">
          {loading ? (
            <p className="loading-text">Loading products...</p>
          ) : error ? (
            <p className="error-text">{error}</p>
          ) : isCartEmpty ? (
            <p className="empty-cart">No products available in the cart.</p>
          ) : (
            cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.pimage} alt={item.pname} className="item-image" />
                <div className="item-details">
                  <h3>{item.pname}</h3>
                  <p className="item-price">${(parseFloat(item.price) * item.quantity).toFixed(2)}</p>
                  <div className="item-actions">
                    <div className="quantity-controls">
                      <button 
                        className="quantity-btn"
                        onClick={() => handleQuantityChange(item.id, Math.max(1, item.quantity - 1))}
                      >
                        −
                      </button>
                      <span className="quantity-display">{item.quantity}</span>
                      <button 
                        className="quantity-btn"
                        onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                    <button 
                      onClick={() => removeItem(item.id)}
                      className="remove-button"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {!isCartEmpty && !loading && !error && (
          <div className="cart-summary">
            <div className="subtotal">
              <span>Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items):</span>
              <span className="total-price">${calculateTotal().toFixed(2)}</span>
            </div>
            <button className="checkout-button" onClick={handleCheckout}>
              Proceed to Checkout
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default CartModal;
