import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/CartModal.css';

export default function CartModal({ isOpen, onClose, cartItems = [], updateQuantity, removeItem }) {
  const navigate = useNavigate();

  if (!isOpen) return null;

  const isCartEmpty = cartItems.length === 0;
  const total = cartItems.reduce((sum, item) => sum + item.pprice * item.quantity, 0);

  const handleCheckout = () => {
    onClose();
    navigate('/checkout');
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Shopping Cart ({cartItems.length} items)</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        <div className="cart-items">
          {isCartEmpty ? (
            <p className="empty-cart">No products available in the cart.</p>
          ) : (
            cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.pname} className="item-image" />
                <div className="item-details">
                  <h3>{item.pname}</h3>
                  <p className="item-price">${(item.pprice * item.quantity).toFixed(2)}</p>
                  <div className="item-actions">
                    <div className="quantity-controls">
                      <button 
                        className="quantity-btn"
                        onClick={() => updateQuantity(item.id, Math.max(1, item.quantity - 1))}
                      >
                        −
                      </button>
                      <span className="quantity-display">{item.quantity}</span>
                      <button 
                        className="quantity-btn"
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                    <button 
                      onClick={() => removeItem(item.id)}
                      className="remove-button"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {!isCartEmpty && (
          <div className="cart-summary">
            <div className="subtotal">
              <span>Subtotal ({cartItems.reduce((sum, item) => sum + item.quantity, 0)} items):</span>
              <span className="total-price">${total.toFixed(2)}</span>
            </div>
            <button className="checkout-button" onClick={handleCheckout}>
              Proceed to Checkout
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
