import { useState, useEffect } from "react";
import axios from "axios";

function useFetchProducts(categoryId) {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        let isMounted = true; // To prevent setting state on unmounted components

        const fetchProducts = async () => {
            try {
                const token = localStorage.getItem("token");

                if (!token) {
                    throw new Error("User is not logged in.");
                }

                const response = await axios.get("https://ram.freelogomaker.in/api/cart", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (isMounted) {
                    setProducts(response.data?.cartproduct || []); // Handle undefined case
                    setLoading(false);
                }
            } catch (error) {
                if (isMounted) {
                    setError(error.message);
                    setLoading(false);
                }
            }
        };

        fetchProducts();

        return () => {
            isMounted = false; // Cleanup function to prevent memory leaks
        };
    }, [categoryId]);

    return { products, loading, error };
}

export default useFetchProducts;
