import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useCart from '../Fetch/Count'; // Import the custom hook
import CartModal from '../Cart/CartModal'; // Import the CartModal component
import WishlistModal from '../Fetch/Order'; // Import the WishlistModal component
import { useNavigate } from 'react-router-dom';

function TopBar() {
  const [wishlistCount, setWishlistCount] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // State for controlling the CartModal and WishlistModal visibility
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isWishlistOpen, setIsWishlistOpen] = useState(false); // Wishlist modal visibility state
  const [search, setSearch] = useState(false);
  const storedData = JSON.parse(localStorage.getItem("orderproducts"));
  const orderproducts = storedData?.products || []; 

  // Using the custom hook to fetch the cart count and order count
  const { cartItems, orderItemsCount, loading } = useCart();

  const cartCount = cartItems.length; // Get cart count from the custom hook
  const orderCount = orderItemsCount.length; // Get order count from the custom hook

  const navigate = useNavigate(); // Use useNavigate for redirect

  useEffect(() => {
    // Set the cart and order count from localStorage
    localStorage.setItem('count', cartCount);
    localStorage.setItem('order', orderCount);

    console.log(cartCount);
    console.log(orderCount);
    console.log(cartItems);
  }, [cartCount, orderCount]); // Effect will run whenever cartCount or orderCount change

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    try {
      const response = await axios.get(`https://ram.freelogomaker.in/public/api/products/search?search=${searchQuery}`);
      setSearchResults(response.data);
      localStorage.setItem('search', JSON.stringify(response.data)); // Store search results in localStorage
      setSearch(true); // Set the search flag to true

      // Use navigate to redirect
      navigate('/search'); // Redirect to the search page
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  // Handle opening the CartModal when the cart icon is clicked
  const handleCartClick = () => {
    setIsCartOpen(true); // Open the cart modal
  };

  // Handle opening the WishlistModal when the wishlist icon is clicked
  const handleWishlistClick = () => {
    setIsWishlistOpen(true); // Open the wishlist modal
  };

  // Handle closing the CartModal
  const handleCloseCart = () => {
    setIsCartOpen(false); // Close the cart modal
  };

  // Handle closing the WishlistModal
  const handleCloseWishlist = () => {
    setIsWishlistOpen(false); // Close the wishlist modal
  };

  return (
    <div className="container-fluid">
      <div className="row bg-secondary py-2 px-xl-5">
        <div className="col-lg-6 d-none d-lg-block">
          <div className="d-inline-flex align-items-center">
            <span className="text-muted px-2">|</span>
            <a className="text-dark" href="/contact">Contact</a>
            <span className="text-muted px-2">|</span>
            <a className="text-dark" href="#">About</a>
          </div>
        </div>
        <div className="col-lg-6 text-center text-lg-right">
          <div className="d-inline-flex align-items-center">
            <a className="text-dark px-2" href="https://www.facebook.com/" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-facebook-f"></i>
            </a>
            <a className="text-dark px-2" href="https://twitter.com/" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-twitter"></i>
            </a>
            <a className="text-dark px-2" href="https://www.linkedin.com/" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-linkedin-in"></i>
            </a>
            <a className="text-dark px-2" href="https://www.instagram.com/" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-instagram"></i>
            </a>
            <a className="text-dark pl-2" href="https://www.youtube.com/" target="_blank" rel="noopener noreferrer">
              <i className="fab fa-youtube"></i>
            </a>
          </div>
        </div>
      </div>

      <div className="row align-items-center py-3 px-xl-5">
        <div className="col-lg-3 d-none d-lg-block">
          <a href="/" className="text-decoration-none">
            <h1 className="m-0 display-5 font-weight-semi-bold">
              <span className="text-primary font-weight-bold border px-3 mr-1">E</span>Shopper
            </h1>
          </a>
        </div>
        <div className="col-lg-6 col-6 text-left">
          <form onSubmit={handleSearch}>
            <div className="input-group">
              <input 
                type="text" 
                className="form-control" 
                placeholder="Search for products" 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <div className="input-group-append">
                <button type="submit" className="input-group-text bg-transparent text-primary">
                  <i className="fa fa-search"></i>
                </button>
              </div>
            </div>
          </form>
          {searchResults.length > 0 && (
            <div className="search-results">
              <ul>
                {searchResults.map((item) => (
                  <li key={item.id}><a href={`/product/${item.id}`}>{item.name}</a></li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <div className="col-lg-3 col-6 text-right">
          <a 
            href="#"
            onClick={handleWishlistClick}  // Handle wishlist icon click to open the modal
            className="btn border"
          >
            <i className="fas fa-heart text-primary"></i>
            <span className="badge wishlist-badge">{wishlistCount > 0 ? wishlistCount : ''}</span>
          </a>
          <a 
            href="#"
            onClick={handleCartClick}  // Handle cart icon click to open the modal
            className="btn border"
          >
            <i className="fas fa-shopping-cart text-primary"></i>
            <span className="badge cart-badge">{cartCount > 0 ? cartCount : ''}</span>
          </a>
        </div>
      </div>

      {/* Cart Modal (conditionally rendered) */}
      {isCartOpen && (
        <CartModal 
          isOpen={isCartOpen} 
          onClose={handleCloseCart} 
          cartItems={cartItems} // Pass the cart items to the modal
        />
      )}

      {/* Wishlist Modal (conditionally rendered) */}
      {isWishlistOpen && (
        <WishlistModal 
          isOpen={isWishlistOpen} 
          onClose={handleCloseWishlist} 
          cartItems={orderproducts} // Pass the wishlist items to the modal
        />
      )}
    </div>
  );
}

export default TopBar;
