import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Swal from "sweetalert2";

const ProductDetail = () => {
    const { id } = useParams();
    const [quantity, setQuantity] = useState(1);
    const [product, setProduct] = useState(null);

    // Fetch product details on component mount or when id changes
    useEffect(() => {
        const fetchProductDetails = async () => {
            const token = localStorage.getItem('token');
            const url = `https://ram.freelogomaker.in/api/product/${id}`;

            try {
                const response = await fetch(url, {
                    headers: { 'Authorization': `Bearer ${token}`, quantity: quantity },
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch product details');
                }

                const data = await response.json();
                setProduct(data.product);
            } catch (error) {
                console.error("Error fetching product:", error);
                Swal.fire({
                    title: "Error",
                    text: "Failed to load product details.",
                    icon: "error",
                });
            }
        };

        fetchProductDetails();
    }, [id]);

    // Handlers for quantity adjustment
    const handleIncrease = () => {
        if (quantity < product?.pstock) {
            setQuantity(quantity + 1);
        }
    };

    const handleDecrease = () => {
        if (quantity > 1) {
            setQuantity(quantity - 1);
        }
    };

    // Share URLs for social media
    const productUrl = `https://yourwebsite.com/product/${product?.id}`;
    const productTitle = product?.pname;
    const productImage = `https://yourwebsite.com/${product?.pimage}`;

    return (
        <div className="container-fluid py-5">
            <div className="row px-xl-5">

                {/* Product Image Section */}
                <div className="col-lg-6 pb-5">
                    <div id="product-carousel" className="carousel slide" data-ride="carousel">
                        <div className="carousel-inner border">
                            <div className="carousel-item active">
                                <img
                                    className="img-fluid w-100"
                                    src={`/${product?.pimage}`}
                                    alt={product?.pname}
                                    style={{ width: '90%', height: '500px', objectFit: 'fill' }}
                                />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Product Details Section */}
                <div className="col-lg-6 pb-5">
                    <h3 className="font-weight-semi-bold" style={{ fontSize: '2rem' }}>{product?.pname}</h3>

                    {/* Product Reviews */}
                    <div className="d-flex mb-3">
                        <small className="pt-1">({product?.reviews_count} Reviews)</small>
                    </div>

                    {/* Product Description */}
                    <p className="mb-4" style={{ fontSize: '1.1rem' }}>{product?.pdescription}</p>

                    {/* Product Stock */}
                    <div className="d-flex mb-4">
                        <p className="text-dark font-weight-medium mb-0 mr-3">Stock: {product?.pstock}</p>
                    </div>

                    {/* Quantity Selector */}
                    <div className="d-flex align-items-center mb-4 pt-2">
                        <div className="input-group quantity mr-3" style={{ width: '150px' }}>
                            <div className="input-group-btn">
                                <button className="btn btn-primary btn-minus" onClick={handleDecrease}>
                                    <i className="fa fa-minus"></i>
                                </button>
                            </div>
                            <input
                                type="text"
                                id="quantity"
                                className="form-control bg-secondary text-center"
                                value={quantity}
                                readOnly
                            />
                            <div className="input-group-btn">
                                <button className="btn btn-primary btn-plus mb-21" onClick={handleIncrease}>
                                    <i className="fa fa-plus"></i>
                                </button>
                            </div>
                        </div>
                    </div>

                    {/* Buttons Section */}
                    <div className="d-flex justify-content-start">
                        {/* Add to Cart Button */}
                        <Link to={`/cart/${product?.id}`} className="btn btn-success px-3 ml-2 d-flex align-items-center">
                            <i className="fa fa-shopping-cart mr-1"></i> Add To Cart
                        </Link>

                        {/* Buy Now Button */}
                        <Link to={`/order/${product?.id}`} className="btn btn-success px-3 ml-2 d-flex align-items-center">
                            <i className="fas fa-credit-card mr-1"></i> Buy Now
                        </Link>
                    </div>

                    {/* Social Media Share Links */}
                    <div className="d-flex pt-2">
                        <p className="text-dark font-weight-medium mb-0 mr-2">Share on:</p>
                        <div className="d-inline-flex">
                            {/* Facebook Share */}
                            <a
                                className="text-dark px-2"
                                href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(productUrl)}&title=${encodeURIComponent(productTitle)}`}
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                <i className="fab fa-facebook-f"></i>
                            </a>
                            
                            {/* Twitter Share */}
                            <a
                                className="text-dark px-2"
                                href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(productUrl)}&text=${encodeURIComponent(productTitle)}`}
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                <i className="fab fa-twitter"></i>
                            </a>
                            
                            {/* LinkedIn Share */}
                            <a
                                className="text-dark px-2"
                                href={`https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(productUrl)}`}
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                <i className="fab fa-linkedin-in"></i>
                            </a>
                            
                            {/* Pinterest Share */}
                            <a
                                className="text-dark px-2"
                                href={`https://www.pinterest.com/pin/create/button/?url=${encodeURIComponent(productUrl)}&media=${encodeURIComponent(productImage)}&description=${encodeURIComponent(productTitle)}`}
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                <i className="fab fa-pinterest"></i>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
