import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

function TopBar() {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  // Function to handle search and redirect to search results page
  const handleSearch = () => {
    if (!searchQuery.trim()) return;

    // Navigate to /search with the search query as a URL parameter
    navigate(`/search?q=${searchQuery}`);
  };

  return (
    <div className="container-fluid">
      <div className="row align-items-center py-3 px-xl-5">
        <div className="col-lg-6 col-6 text-left">
          <div className="input-group">
            {/* Convert the form to a link */}
            <a 
              href="#"
              onClick={handleSearch}
              className="d-flex w-100" // Make the link take up the full width like the form
            >
              <input 
                type="text" 
                className="form-control" 
                placeholder="Search for products" 
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <div className="input-group-append">
                <button 
                  type="button" 
                  className="input-group-text bg-transparent text-primary" 
                  onClick={handleSearch} // Button click should also trigger the search
                >
                  <i className="fa fa-search"></i>
                </button>
              </div>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TopBar;
