import React, { useState, useEffect } from 'react';
import { removeCartItem } from '../Fetch/RemoveCart';
import useCartData from '../Fetch/Orders'; 
import Swal from 'sweetalert2';
import '../css/CartModal.css'; // Ensure your CSS is linked correctly.

function CartModal({ isOpen, onClose }) {
  const { cartItems, loading: fetchLoading, error } = useCartData(isOpen); // Fetch ordered items using useCartData hook
  const [localCart, setLocalCart] = useState([]);  
  const [loading, setLoading] = useState(false); 
  const [removeError, setRemoveError] = useState(null); 

  useEffect(() => {
    if (cartItems) {
      setLocalCart(cartItems);  
    }
  }, [cartItems]);

  const handleRemoveItem = async (itemId) => {
    setLoading(true);
    const result = await removeCartItem(itemId);
    if (result.success) {
      setLocalCart((prevCart) => prevCart.filter((item) => item.id !== itemId));
      Swal.fire({
        icon: 'success',
        title: 'Removed!',
        text: 'Item has been removed from your cart.',
        timer: 1500,
        showConfirmButton: false,
      });
    } else {
      setRemoveError('Failed to remove item. Please try again.');
      Swal.fire({
        icon: 'error',
        title: 'Error!',
        text: 'Failed to remove item. Please try again.',
      });
    }
    setLoading(false);
  };

  const calculateTotal = () => {
    return localCart.reduce((sum, item) => {
      const itemPrice = parseFloat(item.price);
      return sum + itemPrice * item.quantity;
    }, 0);
  };

  return (
    <div className={`modal-overlay ${isOpen ? 'show' : ''}`}>
      <div className="modal-content">
        <div className="modal-header">
          <h2>Your Cart</h2>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>

        <div className="cart-items">
          {fetchLoading ? (
            <p className="loading-text">Loading...</p>
          ) : error ? (
            <p className="error-text">{error}</p>
          ) : localCart.length === 0 ? (
            <p className="empty-cart">No items in your cart!</p>
          ) : (
            localCart.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.pimage} alt={item.pname} className="item-image" />
                <div className="item-details">
                  <h3>{item.pname}</h3>
                  <p className="item-price">${(parseFloat(item.price) * item.quantity).toFixed(2)}</p>
                  <p className="item-quantity">Quantity: {item.quantity}</p>
                  <button
                    onClick={() => handleRemoveItem(item.id)}
                    className="remove-button"
                    disabled={loading}
                  >
                    <i className="fas fa-trash-alt"></i> Remove
                  </button>
                </div>
              </div>
            ))
          )}

          {localCart.length > 0 && !fetchLoading && !error && (
            <div className="cart-summary">
              <div className="subtotal">
                <span>Total ({localCart.reduce((sum, item) => sum + item.quantity, 0)} items):</span>
                <span className="total-price">${calculateTotal().toFixed(2)}</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default CartModal;
